mapper.sql:
select o.*
from order o
where 1=1

    and o.consumer_no = #{consumerNo}


    and o.order_status = #{orderStatus}



    and o.saler_user_no = #{salerUserNo}


ORDER BY o.submit_time DESC

select o.*
from order o LEFT JOIN `user` u on
o.saler_user_no = u.user_no
where 1=1

    and o.consumer_no = #{consumerNo}


    and o.order_status = #{orderStatus}


    and o.saler_user_no = #{salerUserNo}


    and u.group_id = #{groupId}

ORDER BY o.submit_time DESC
