mapper.sql:
SELECT s.* FROM `sub_order` s LEFT JOIN `order` o ON o.order_no = s.order_no WHERE 1 = 1
and s.sub_order_status = #{subOrderStatus}
and o.buyer_user_no = #{buyerUserNo}
ORDER BY s.created_time DESC

SELECT s.*
FROM
`sub_order` s
LEFT JOIN `order` o ON o.order_no = s.order_no
LEFT JOIN `user` u ON o.buyer_user_no = u.user_no
WHERE 1 = 1

    and s.sub_order_status = #{subOrderStatus}



    and o.buyer_user_no = #{buyerUserNo}



    and u.group_id = #{groupId}


ORDER BY s.created_time DESC
